boolean Field::operator==(const Field& other_value) const
{
if (!is_bound() && !other_value.is_bound()) return TRUE;
return field_label__==other_value.field_label__
  && field_type__==other_value.field_type__
  && field_var__==other_value.field_var__
  && field_desc==other_value.field_desc
  && field_required==other_value.field_required
  && field_value__list==other_value.field_value__list
  && field_option__list==other_value.field_option__list;
}

boolean Field::is_bound() const
{
if (bound_flag) return TRUE;
if(OPTIONAL_OMIT == field_label__.get_selection() || field_label__.is_bound()) return TRUE;
if(OPTIONAL_OMIT == field_type__.get_selection() || field_type__.is_bound()) return TRUE;
if(OPTIONAL_OMIT == field_var__.get_selection() || field_var__.is_bound()) return TRUE;
if(OPTIONAL_OMIT == field_desc.get_selection() || field_desc.is_bound()) return TRUE;
if(OPTIONAL_OMIT == field_required.get_selection() || field_required.is_bound()) return TRUE;
if(field_value__list.is_bound()) return TRUE;
if(field_option__list.is_bound()) return TRUE;
return FALSE;
}
boolean Field::is_value() const
{
if (!is_bound()) return FALSE;
if(OPTIONAL_OMIT != field_label__.get_selection() && !field_label__.is_value()) return FALSE;
if(OPTIONAL_OMIT != field_type__.get_selection() && !field_type__.is_value()) return FALSE;
if(OPTIONAL_OMIT != field_var__.get_selection() && !field_var__.is_value()) return FALSE;
if(OPTIONAL_OMIT != field_desc.get_selection() && !field_desc.is_value()) return FALSE;
if(OPTIONAL_OMIT != field_required.get_selection() && !field_required.is_value()) return FALSE;
if(!field_value__list.is_value()) return FALSE;
if(!field_option__list.is_value()) return FALSE;
return TRUE;
}
