boolean Field_template::match(const Field& other_value) const
{
if (!other_value.is_bound()) return FALSE;
switch (template_selection) {
case ANY_VALUE:
case ANY_OR_OMIT:
return TRUE;
case OMIT_VALUE:
return FALSE;
case SPECIFIC_VALUE:
if(!other_value.label__().is_bound()) return FALSE;
if((other_value.label__().ispresent() ? !single_value->field_label__.match((const UNIVERSAL_CHARSTRING&)other_value.label__()) : !single_value->field_label__.match_omit()))return FALSE;
if(!other_value.type__().is_bound()) return FALSE;
if((other_value.type__().ispresent() ? !single_value->field_type__.match((const Field_type__&)other_value.type__()) : !single_value->field_type__.match_omit()))return FALSE;
if(!other_value.var__().is_bound()) return FALSE;
if((other_value.var__().ispresent() ? !single_value->field_var__.match((const UNIVERSAL_CHARSTRING&)other_value.var__()) : !single_value->field_var__.match_omit()))return FALSE;
if(!other_value.desc().is_bound()) return FALSE;
if((other_value.desc().ispresent() ? !single_value->field_desc.match((const UNIVERSAL_CHARSTRING&)other_value.desc()) : !single_value->field_desc.match_omit()))return FALSE;
if(!other_value.required().is_bound()) return FALSE;
if((other_value.required().ispresent() ? !single_value->field_required.match((const Empty&)other_value.required()) : !single_value->field_required.match_omit()))return FALSE;
if(!other_value.value__list().is_bound()) return FALSE;
if(!single_value->field_value__list.match(other_value.value__list()))return FALSE;
if(!other_value.option__list().is_bound()) return FALSE;
if(!single_value->field_option__list.match(other_value.option__list()))return FALSE;
return TRUE;
case VALUE_LIST:
case COMPLEMENTED_LIST:
for (unsigned int list_count = 0; list_count < value_list.n_values; list_count++)
if (value_list.list_value[list_count].match(other_value)) return template_selection == VALUE_LIST;
return template_selection == COMPLEMENTED_LIST;
default:
TTCN_error("Matching an uninitialized/unsupported template of type @jabber_x_data.Field.");
}
return FALSE;
}

boolean Field_template::is_bound() const
{
if (template_selection == UNINITIALIZED_TEMPLATE && !is_ifpresent) return FALSE;
if (template_selection != SPECIFIC_VALUE) return TRUE;
if (single_value->field_label__.is_omit() || single_value->field_label__.is_bound()) return TRUE;
if (single_value->field_type__.is_omit() || single_value->field_type__.is_bound()) return TRUE;
if (single_value->field_var__.is_omit() || single_value->field_var__.is_bound()) return TRUE;
if (single_value->field_desc.is_omit() || single_value->field_desc.is_bound()) return TRUE;
if (single_value->field_required.is_omit() || single_value->field_required.is_bound()) return TRUE;
if (single_value->field_value__list.is_bound()) return TRUE;
if (single_value->field_option__list.is_bound()) return TRUE;
return FALSE;
}

boolean Field_template::is_value() const
{
if (template_selection != SPECIFIC_VALUE || is_ifpresent) return FALSE;
if (!single_value->field_label__.is_omit() && !single_value->field_label__.is_value()) return FALSE;
if (!single_value->field_type__.is_omit() && !single_value->field_type__.is_value()) return FALSE;
if (!single_value->field_var__.is_omit() && !single_value->field_var__.is_value()) return FALSE;
if (!single_value->field_desc.is_omit() && !single_value->field_desc.is_value()) return FALSE;
if (!single_value->field_required.is_omit() && !single_value->field_required.is_value()) return FALSE;
if (!single_value->field_value__list.is_value()) return FALSE;
if (!single_value->field_option__list.is_value()) return FALSE;
return TRUE;
}

void Field_template::clean_up()
{
switch (template_selection) {
case SPECIFIC_VALUE:
delete single_value;
break;
case VALUE_LIST:
case COMPLEMENTED_LIST:
delete [] value_list.list_value;
default:
break;
}
template_selection = UNINITIALIZED_TEMPLATE;
}

Field Field_template::valueof() const
{
if (template_selection != SPECIFIC_VALUE || is_ifpresent)
TTCN_error("Performing valueof or send operation on a non-specific template of type @jabber_x_data.Field.");
Field ret_val;
if (single_value->field_label__.is_omit()) ret_val.label__() = OMIT_VALUE;
else if (single_value->field_label__.is_bound()) {
ret_val.label__() = single_value->field_label__.valueof();
}
if (single_value->field_type__.is_omit()) ret_val.type__() = OMIT_VALUE;
else if (single_value->field_type__.is_bound()) {
ret_val.type__() = single_value->field_type__.valueof();
}
if (single_value->field_var__.is_omit()) ret_val.var__() = OMIT_VALUE;
else if (single_value->field_var__.is_bound()) {
ret_val.var__() = single_value->field_var__.valueof();
}
if (single_value->field_desc.is_omit()) ret_val.desc() = OMIT_VALUE;
else if (single_value->field_desc.is_bound()) {
ret_val.desc() = single_value->field_desc.valueof();
}
if (single_value->field_required.is_omit()) ret_val.required() = OMIT_VALUE;
else if (single_value->field_required.is_bound()) {
ret_val.required() = single_value->field_required.valueof();
}
if (single_value->field_value__list.is_bound()) {
ret_val.value__list() = single_value->field_value__list.valueof();
}
if (single_value->field_option__list.is_bound()) {
ret_val.option__list() = single_value->field_option__list.valueof();
}
return ret_val;
}

