const char *Field_type__::enum_to_str(enum_type enum_par, boolean txt)
{
switch (enum_par) {
case boolean__: if (txt) return "boolean"; else return "boolean_";
case fixed: return "fixed";
case hidden: return "hidden";
case jid__multi: if (txt) return "jid-multi"; else return "jid_multi";
case jid__single: if (txt) return "jid-single"; else return "jid_single";
case list__multi: if (txt) return "list-multi"; else return "list_multi";
case list__single: if (txt) return "list-single"; else return "list_single";
case text__multi: if (txt) return "text-multi"; else return "text_multi";
case text__private: if (txt) return "text-private"; else return "text_private";
case text__single: if (txt) return "text-single"; else return "text_single";
default: return "<unknown>";
}
}

Field_type__::enum_type Field_type__::str_to_enum(const char *str_par)
{
if (!strcmp(str_par, "boolean") || !strcmp(str_par, "boolean_")) return boolean__;
else if (!strcmp(str_par, "fixed")) return fixed;
else if (!strcmp(str_par, "hidden")) return hidden;
else if (!strcmp(str_par, "jid-multi") || !strcmp(str_par, "jid_multi")) return jid__multi;
else if (!strcmp(str_par, "jid-single") || !strcmp(str_par, "jid_single")) return jid__single;
else if (!strcmp(str_par, "list-multi") || !strcmp(str_par, "list_multi")) return list__multi;
else if (!strcmp(str_par, "list-single") || !strcmp(str_par, "list_single")) return list__single;
else if (!strcmp(str_par, "text-multi") || !strcmp(str_par, "text_multi")) return text__multi;
else if (!strcmp(str_par, "text-private") || !strcmp(str_par, "text_private")) return text__private;
else if (!strcmp(str_par, "text-single") || !strcmp(str_par, "text_single")) return text__single;
else return UNKNOWN_VALUE;
}

