{
field_choice.clean_up();
bound_flag = FALSE;
}

Bind& Bind::operator=(const Bind& other_value)
{
if (this != &other_value) {
  if(!other_value.is_bound()) TTCN_error("Assignment of an unbound value of type @urn_ietf_params_xml_ns_xmpp_bind.Bind.");
  bound_flag = TRUE;
  if (other_value.choice().is_bound()) field_choice = other_value.choice();
  else field_choice.clean_up();
}
return *this;
}

boolean Bind::operator==(const Bind& other_value) const
{
if (!is_bound() && !other_value.is_bound()) return TRUE;
return field_choice==other_value.field_choice;
}

